mapper.select:
select job.*,company.company_name,company.company_location,company.company_description,industry.industry_name from job,company,industry
and job.industry_id=industry.industry_id and job.company_id=company.company_id
limit #{page.pageNums},#{page.pageSize}

select job.*,company.company_name,company.company_location,company.company_description,industry.industry_name from job,company,industry where job.company_id=#{job.companyId}
and job_name like "%${job.jobName}%"
and job.job_subtime>#{queryTime}
and job.company_id=company.company_id and job.industry_id=industry.industry_id
